import React, { Component } from 'react';
import { useLocation } from 'react-router-dom'
import { Link } from 'react-router-dom';

import LoggedInTester from '../../buttons/LoggedInTester'
import Karma from '../Karma'
import styles from '../../styles/TestProfile.module.css'

import pfp from '../../icons/pfp.png'

class TestProfile extends Component {
  constructor(props) {
    super(props);
    this.state = { foo: [], };
  }

  componentDidMount() {
    const headers = {"Content-Type": "application/json"};

    if (localStorage.getItem('auth-token')) {
        headers["Authorization"] = localStorage.getItem('auth-token');
    }

    fetch('http://127.0.0.1:8000/connect/get_info/', { headers, })
        .then(response => response.json())
        .then((data) => {
            this.setState({ foo: data });
            console.log(data);
        })
    .catch(console.log)
  }


  ShowProfile = ({ foo }) => {
    return (<>
      <div className={styles.profile_wrapper2}>      
        <img src={pfp} alt="pfp" className={styles.pfp}></img>
        <div className={styles.profile_info_wrapper}>
          <p>ID: {foo['id']}</p>
          <p className={styles.name}>
            {foo['first_name']}
            {' '}
            {foo['last_name']}
          </p>
          <p>College: {foo['user_college']}</p>
          <p>Major: {foo['user_major']}</p>
          <p>Karma: {foo['user_karma']}</p>
          <hr className={styles.solid}></hr>
          <div className={styles.contact_info_wrapper}>
            <div>
              <p className={styles.contact_type}>Email:</p>
              <this.ShowContactInfo contact={foo['email']}/>
            </div>
            <div>
              <p className={styles.contact_type}>Phone:</p>
              <this.ShowContactInfo contact={foo['phone']}/>
            </div>
            <div>
              <p className={styles.contact_type}>Discord:</p>
              <this.ShowContactInfo contact={foo['discord']}/>
            </div>

                    <div><p className={styles.contact_type}>Instagram:</p>
                    <this.ShowContactInfo contact={foo['ig']}/></div>
          </div>
        </div>
      </div>
    </>);
  } 


  render() {
    return (<>
      <div className={styles.componentWrapper}>
        <LoggedInTester />
        {/* Sufficient to get whatever info we need from user */}
        { localStorage.getItem('auth-token') }
      </div>

      <div className={styles.wrapper}>
        <div className={styles.profile_wrapper}>
          <this.ShowProfile foo={this.state.foo} />
        </div>
      </div>
    </>);
  }
}

export default TestProfile
